let counterLabel=document.getElementById("counter"),count=0,username=document.getElementById("username").value;function*generate(e){if(e.length<=1)yield e;else{let t=e[0],n=e.slice(1);for(let e of generate(n))yield t+e,yield t+"."+e}}function updateEmails(){let e=document.getElementById("username").value;document.getElementById("emails").value="",count=0;let t=new Date;for(let t of generate(e))document.getElementById("emails").value+=t+"@gmail.com\r\n",count+=1,counterLabel.innerText=count;document.getElementById("emails").value=document.getElementById("emails").value.slice(0,-1);let n=new Date-t;console.log("Finished in "+n+"ms")}document.getElementById("username").oninput=function(){let e=document.getElementById("username").value;username!==e&&(username=e,username.length>1&&setTimeout(updateEmails,150))};const fileNameInput=document.getElementById("username"),selectMenu=document.getElementById("save-as"),saveBtn=document.getElementById("saveBtn");selectMenu.addEventListener("change",(()=>{const e=selectMenu.options[selectMenu.selectedIndex].text;saveBtn.innerText=`Save As ${e.split(" ")[0]} File`})),saveBtn.addEventListener("click",(()=>{const e=new Blob([document.getElementById("emails").value],{type:selectMenu.value}),t=URL.createObjectURL(e),n=document.createElement("a");n.download=fileNameInput.value,n.href=t,n.click()}));